  --sidebar-border: 250 18% 90%;
  --sidebar-primary: 30 100% 60%;
  --sidebar-primary-foreground: 0 0% 100%;
  --sidebar-accent: 250 18% 90%;
  --sidebar-accent-foreground: 260 15% 15%;
  --sidebar-ring: 30 100% 60%;

  --popover: 0 0% 100%;
  --popover-foreground: 260 15% 15%;
  --popover-border: 250 12% 90%;

  --primary: 30 100% 60%;
  --primary-foreground: 0 0% 100%;

  --secondary: 250 15% 92%;
  --secondary-foreground: 260 15% 18%;

  --muted: 250 12% 94%;
  --muted-foreground: 260 8% 45%;

  --accent: 128 100% 28%;
  --accent-foreground: 0 0% 100%;

  --destructive: 0 84% 60%;
  --destructive-foreground: 0 20% 98%;

  --input: 250 12% 88%;
  --ring: 260 85% 58%;

  --chart-1: 30 100% 60%;
  --chart-2: 128 100% 28%;
  --chart-3: 0 0% 100%;
  --chart-4: 200 85% 55%;
  --chart-5: 340 75% 58%;

  --font-sans: 'Inter', -apple-system, BlinkMacSystemFont, sans-serif;
  --font-display: 'Space Grotesk', 'Inter', sans-serif;
  --font-mono: 'JetBrains Mono', Menlo, monospace;

  --radius: .75rem;

  --shadow-2xs: 0px 1px 2px 0px hsl(260 15% 10% / 0.05);
  --shadow-xs: 0px 1px 3px 0px hsl(260 15% 10% / 0.08);
  --shadow-sm: 0px 2px 4px 0px hsl(260 15% 10% / 0.06), 0px 1px 2px -1px hsl(260 15% 10% / 0.04);
  --shadow: 0px 4px 6px -1px hsl(260 15% 10% / 0.08), 0px 2px 4px -2px hsl(260 15% 10% / 0.06);
  --shadow-md: 0px 6px 12px -2px hsl(260 15% 10% / 0.10), 0px 3px 6px -3px hsl(260 15% 10% / 0.08);
  --shadow-lg: 0px 10px 20px -4px hsl(260 15% 10% / 0.12), 0px 4px 8px -4px hsl(260 15% 10% / 0.10);
  --shadow-xl: 0px 20px 30px -6px hsl(260 15% 10% / 0.14), 0px 8px 12px -6px hsl(260 15% 10% / 0.12);
  --shadow-2xl: 0px 30px 50px -8px hsl(260 15% 10% / 0.18);

  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

.dark {
  --button-outline: rgba(255,255,255, .10);
  --badge-outline: rgba(255,255,255, .05);

  --opaque-button-border-intensity: 9;

  --elevate-1: rgba(255,255,255, .04);
  --elevate-2: rgba(255,255,255, .09);

  --background: 260 20% 6%;
  --foreground: 250 10% 95%;

  --border: 260 15% 16%;

  --card: 260 18% 10%;
  --card-foreground: 250 10% 92%;
  --card-border: 260 15% 14%;

  --sidebar: 260 20% 8%;
  --sidebar-foreground: 250 10% 90%;
  --sidebar-border: 260 15% 12%;
  --sidebar-primary: 30 100% 60%;
  --sidebar-primary-foreground: 0 0% 100%;
  --sidebar-accent: 260 15% 14%;
  --sidebar-accent-foreground: 250 10% 88%;
  --sidebar-ring: 30 100% 60%;

  --popover: 260 18% 12%;
  --popover-foreground: 250 10% 90%;
  --popover-border: 260 15% 18%;

  --primary: 30 100% 60%;
  --primary-foreground: 0 0% 100%;

  --secondary: 260 12% 18%;
  --secondary-foreground: 250 10% 92%;

  --muted: 260 12% 16%;
  --muted-foreground: 250 8% 65%;

  --accent: 128 100% 28%;
  --accent-foreground: 0 0% 100%;

  --destructive: 0 84% 60%;
  --destructive-foreground: 0 20% 98%;

  --input: 260 15% 20%;
  --ring: 260 85% 58%;

  --chart-1: 30 100% 65%;
  --chart-2: 128 100% 35%;
  --chart-3: 0 0% 100%;
  --chart-4: 200 85% 60%;
  --chart-5: 340 75% 65%;

  --shadow-2xs: 0px 1px 2px 0px hsl(260 15% 4% / 0.30);
  --shadow-xs: 0px 1px 3px 0px hsl(260 15% 4% / 0.40);
  --shadow-sm: 0px 2px 4px 0px hsl(260 15% 4% / 0.35), 0px 1px 2px -1px hsl(260 15% 4% / 0.25);
  --shadow: 0px 4px 6px -1px hsl(260 15% 4% / 0.45), 0px 2px 4px -2px hsl(260 15% 4% / 0.35);
  --shadow-md: 0px 6px 12px -2px hsl(260 15% 4% / 0.50), 0px 3px 6px -3px hsl(260 15% 4% / 0.40);
  --shadow-lg: 0px 10px 20px -4px hsl(260 15% 4% / 0.55), 0px 4px 8px -4px hsl(260 15% 4% / 0.45);
  --shadow-xl: 0px 20px 30px -6px hsl(260 15% 4% / 0.60), 0px 8px 12px -6px hsl(260 15% 4% / 0.50);
  --shadow-2xl: 0px 30px 50px -8px hsl(260 15% 4% / 0.70);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

@layer utilities {
  /* Display Font */
  .font-display {
    font-family: var(--font-display);
  }

  /* Glassmorphism Effects */
  .glass {
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.2);
  }

  .dark .glass {
    background: rgba(255, 255, 255, 0.05);
    border: 1px solid rgba(255, 255, 255, 0.1);
  }

  .glass-strong {
    background: rgba(255, 255, 255, 0.15);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.3);
  }

  .dark .glass-strong {
    background: rgba(255, 255, 255, 0.08);
    border: 1px solid rgba(255, 255, 255, 0.15);
  }

  /* Gradient Text - Indian Tricolor */
  .gradient-text {
    background: linear-gradient(90deg, 
      hsl(30, 100%, 60%) 0%,
      hsl(0, 0%, 100%) 50%,
      hsl(128, 100%, 28%) 100%
    );
    background-clip: text;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
  }

  .gradient-text-purple {
    @apply bg-gradient-to-r from-purple-500 to-pink-500 bg-clip-text text-transparent;
  }

  /* Gradient Backgrounds - Indian Tricolor */
  .gradient-bg {
    background: linear-gradient(135deg, 
      hsl(30, 100%, 60%, 0.2) 0%,
      hsl(0, 0%, 100%, 0.1) 50%,
      hsl(128, 100%, 28%, 0.1) 100%
    );
  }

  .gradient-primary {
    background: linear-gradient(90deg, 
      hsl(30, 100%, 60%) 0%,
      hsl(0, 0%, 100%) 50%,
      hsl(128, 100%, 28%) 100%
    );
  }

  .gradient-card {
    background: linear-gradient(135deg, 
      hsl(var(--card)) 0%, 
      hsl(var(--card)) 50%, 
      hsl(var(--primary) / 0.05) 100%
    );
  }

  .dark .gradient-card {
    background: linear-gradient(135deg, 
      hsl(var(--card)) 0%, 
      hsl(var(--card)) 50%, 
      hsl(var(--primary) / 0.1) 100%
    );
  }

  /* Animated Gradient Border */
  @keyframes gradient-rotate {
    0% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
    100% { background-position: 0% 50%; }
  }

  .gradient-border {
    position: relative;
    background: hsl(var(--card));
    border-radius: var(--radius);
  }

  .gradient-border::before {
    content: "";
    position: absolute;
    inset: 0;
    border-radius: var(--radius);
    padding: 2px;
    background: linear-gradient(90deg, 
      hsl(30, 100%, 60%) 0%,
      hsl(0, 0%, 100%) 25%,
      hsl(128, 100%, 28%) 50%,
      hsl(0, 0%, 100%) 75%,
      hsl(30, 100%, 60%) 100%
    );
    background-size: 200% 200%;
    -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    -webkit-mask-composite: xor;
    mask-composite: exclude;
    animation: gradient-rotate 3s ease infinite;
  }

  /* Story Ring Gradient - Indian Tricolor */
  .story-ring {
    background: linear-gradient(135deg, 
      hsl(30, 100%, 60%) 0%,
      hsl(0, 0%, 100%) 50%,
      hsl(128, 100%, 28%) 100%
    );
    padding: 3px;
    border-radius: 50%;
  }

  .story-ring-viewed {
    background: hsl(var(--muted));
    padding: 3px;
    border-radius: 50%;
  }

  /* Smooth Animations */
  @keyframes fade-in {
    from { opacity: 0; transform: translateY(10px); }
    to { opacity: 1; transform: translateY(0); }
  }

  @keyframes slide-up {
    from { transform: translateY(100%); }
    to { transform: translateY(0); }
  }

  @keyframes scale-in {
    from { opacity: 0; transform: scale(0.9); }
    to { opacity: 1; transform: scale(1); }
  }

  .animate-fade-in {
    animation: fade-in 0.3s ease-out;
  }

  .animate-slide-up {
    animation: slide-up 0.3s ease-out;
  }

  .animate-scale-in {
    animation: scale-in 0.2s ease-out;
  }

  /* Smooth Scroll */
  .smooth-scroll {
    scroll-behavior: smooth;
    -webkit-overflow-scrolling: touch;
  }

  /* Hide Scrollbar but keep functionality */
  .hide-scrollbar::-webkit-scrollbar {
    display: none;
  }

  .hide-scrollbar {
    -ms-overflow-style: none;
    scrollbar-width: none;
  }

  /* Search Cancel Button */
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  /* Placeholder for contentEditable */
  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  /* Elevation System */
  .no-default-hover-elevate {}
  .no-default-active-elevate {}

  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: -1;
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  .border.toggle-elevate::before {
    inset: -1px;
  }

  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: 999;
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after {
    inset: -1px;
  }
}

